sysauto_script.script:
var gpp = new GlideRecord('x_323921_script_te_cmdb_ci_vm_instance_demo');
gpp.addQuery('state','!=','on');
gpp.query();
while(gpp.next()){

	a = gpp.ip_address;
	var gr = new GlideRecord('incident');

	gr.addQuery('request',current);
	//gr.initialize();

	gr.comments = 'autogenerated incident because state is off for ip:'+a;
	gr.assigned_to = 'System Administrator';
	gr.short_description = 'autogenerated incident because state is off for ip:'+a;
	// gr.field_name = 'value';     ---> Here is how you'd add a specified field value


	gr.insert();
}
