	function createInstanceRec() {
		workflow.info(current.variables.name);
		var gp = new GlideRecord('x_323921_script_te_cmdb_ci_vm_instance_demo');
		gp.addQuery('request',current);
		//gp.initialize();
		//gp.query();
		gp.name = name+"_"+uni;
		gp.vm_inst_id = name+"."+uni;
		gp.owned_by = own;
		gp.state = 'on';
		gp.ip_address = b;
		gp.cost = pri;
		//gp.busines_criticality = current.variables.busines_criticality;
		//workflow.info(response_ci);
		gp.insert();
	}

